import 'element-ui/lib/theme-chalk/index.css';
import store from "./../store";
export default {
  data: function data() {
    return {
      findCls: "",
      reservationCls: "",
      mineCls: ""
    };
  },
  methods: {
    findBtn: function findBtn() {
      this.$store.commit('setFind');
      this.changeColor();
    },
    reservationBtn: function reservationBtn() {
      this.$store.commit('setRes');
      this.changeColor();
    },
    mineBtn: function mineBtn() {
      this.$store.commit('setMine');
      this.changeColor();
    },
    initColor: function initColor() {
