    },
    changeColor: function changeColor() {
      switch (this.$store.state.position) {
        case "findroom":
          this.findCls += " footer-active";
          break;

        case "resveration":
          this.reservationCls += " footer-active";
          break;

        case "mine":
          this.mineCls += " footer-active";
          break;
      }
    }
  },
  created: function created() {
    this.initColor();
    this.changeColor();
  }
};
