export default {
    data() {
        return {
            tableData: [],
        };
    },
    methods: {
        deleteRoom(roomId) {
            // 删除房间
            this.$confirm("此操作将永久删除该文件, 是否继续?", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning",
            })
                .then(() => {
                    this.axios
                        .post(
                            "http://localhost:9151/admin/deleteRoom?roomId=" +
                                roomId
                        )
                        .then((res) => {
                            this.$message({
                                type: "success",
                                message: "删除成功",
                            });
                            this.listRooms();
                        })
                        .catch((res) => {
                            this.$message({
                                type: "error",
                                message: "删除失败",
                            });
                        });
                })
                .catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消删除",
                    });
                });
        },
        listRooms() {
            this.axios
                .get("http://localhost:9151/admin/listRooms")
                .then((res) => {
                    // console.log(res.data.data);
                    this.tableData = res.data.data;
                    for (var i = 0; i < this.tableData.length; ++i) {
                        // 房间状态
                        if (this.tableData[i].state == 0) {
                            this.tableData[i].state = "空闲";
                        } else if (this.tableData[i].state == 1) {
                            this.tableData[i].state = "已入住";
                        }

                        // 房间类型
                        switch (this.tableData[i].type) {
                            case 1:
                                this.tableData[i].type = "单人房";
                                break;
                            case 2:
                                this.tableData[i].type = "双人房";
                                break;
                            case 3:
                                this.tableData[i].type = "三人房";
                                break;
                            case 4:
                                this.tableData[i].type = "大床房";
                                break;
                            case 5:
                                this.tableData[i].type = "豪华套房";
                                break;
                        }
                    }
                })
                .catch((res) => {
                    console.log("err: " + res);
                });
        },
    },
    mounted() {
        this.listRooms();
    },
};
