result:
$bg: #1f2b3a;
$dark_gray: #2d3a4b;
$light_gray: #e4e9f2;
$btn_hover: #198754;
$btn_bg: #0d6efd;

.login-container {
    height: 100%;
    background: linear-gradient(135deg, #2c3e50, #4ca1af);
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;

    .box-card {
        background: rgba(255, 255, 255, 0.9);
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
        padding: 40px;
        border-radius: 15px;
        width: 400px;
        position: relative;
        z-index: 10;
        transition: transform 0.3s ease;
    }

    .title-container {
        margin-bottom: 20px;
        text-align: center;
        h2 {
            font-size: 30px;
            font-weight: bold;
            color: $dark_gray;
            margin-bottom: 20px;
        }
    }

    .el-form-item {
        margin-bottom: 20px;
        position: relative;
    }

    .el-input {
        background: rgba(255, 255, 255, 0.2);
        border-radius: 10px;
        color: $light_gray;

        input {
            background: transparent;
            border: none;
            color: $light_gray;
            padding-left: 35px;
            height: 45px;
            border-radius: 10px;
            caret-color: $btn_bg;

            &::placeholder {
                color: $light_gray;
            }
        }

        &:focus-within {
            .el-input__inner {
                background: rgba(255, 255, 255, 0.4);
            }
        }
    }

    .show-pwd {
        position: absolute;
        right: 10px;
        top: 10px;
        cursor: pointer;
        color: $dark_gray;
        font-size: 18px;
    }

    .el-button {
        background: linear-gradient(90deg, $btn_bg, #198754);
        color: #fff;
        font-size: 18px;
        padding: 12px 0;
        width: 100%;
        border-radius: 25px;
        transition: transform 0.3s ease;

        &:hover {
            background: linear-gradient(90deg, #198754, $btn_bg);
            transform: scale(1.05);
        }
    }
}
