export default {
    data() {
        return {
            addnew: {
                number: "",
                type: "",
                state: "",
                maxPeople: "",
                introduce: "",

                size: "",
                computer: "有",
                hotWater: "有",
                wifi: "有",
                tv: "有",
                breakfast: "有",
            },
            iconstyle: "el-icon-document-checked",
            disabled: false,
            btnType: "primary",
            isRealaddnew: false,
            dialogVisible: false,
        };
    },
    components: {},
    methods: {
        addnewBtn() {
            if (this.addnew.number == "") {
                this.$message.error("请提供房间号");
                return;
            }
            if (this.addnew.type == "") {
                this.$message.error("请选择房间类型");
                return;
            }
            if (this.addnew.state === "") {
                this.$message.error("请选择房间状态");
                return;
            }
            if (this.addnew.maxPeople == "") {
                this.$message.error("请填写最大容纳人数");
                return;
            }
            if (this.addnew.size == "") {
                this.$message.error("请填写房间面积");
                return;
            }
            this.addnew.introduce =
                "面积:" +
                this.addnew.size +
                "m²,容纳:1-" +
                this.addnew.maxPeople +
                "人,电脑:" +
                this.addnew.computer +
                ",热水:" +
                this.addnew.hotWater +
                ",WIFI:" +
                this.addnew.wifi +
                ",电视:" +
                this.addnew.tv +
                ",早餐:" +
                this.addnew.breakfast;
            this.axios
                .post("http://localhost:9151/admin/addRoom", this.addnew)
                .then((res) => {
                    if (res.data.code == "200") {
                        this.dialogVisible = true;
                        this.addnew = {
                            number: "",
                            type: "",
                            state: "",
                            maxPeople: "",
                            introduce: "",

                            size: "",
                            people: "",
                            computer: "有",
                            hotWater: "有",
                            wifi: "有",
                            tv: "有",
                            breakfast: "有",
                        };
                    } else {
                        this.$message.error(res.data.data);
                    }
                })
                .catch((res) => {
                    console.log("err: " + res);
                });
        },
    },
};
