.user-table {
    padding: 20px;
    background: linear-gradient(135deg, #e0f7fa, #80deea);
    min-height: 100vh;
}
.order-card {
    margin-bottom: 20px;
}
.order-card-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #ffffff;
    border-radius: 15px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}
.order-card-container:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.3);
}
.order-info h3 {
    font-size: 20px;
    color: #00796b;
}
.order-info p {
    margin: 5px 0;
    font-size: 14px;
    color: #455a64;
}
.order-actions {
    display: flex;
    flex-direction: column;
    gap: 10px;
}
.action-btn {
    width: 120px;
    font-size: 16px;
    font-weight: bold;
}
.action-btn:hover {
    transform: scale(1.05);
}
