body {
  background: linear-gradient(135deg, #f6d365, #fda085);
  font-family: 'Roboto', sans-serif;
  color: #333;
}

.change-form {
  background: #fff;
  border-radius: 16px;
  padding: 3rem 4rem; /* 内部增加间距 */
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  margin: 3rem auto; /* 外部增加间距 */
  max-width: 800px; /* 增加盒子的宽度 */
}

.change-title {
  font-size: 1.8rem; /* 标题更大 */
  font-weight: bold;
  text-align: center;
  color: #4a4a4a;
  margin-bottom: 2rem; /* 增加标题的下间距 */
}

.el-input,
.el-select {
  border-radius: 8px;
}

.query-btn,
.change-btn {
  width: 100%;
  font-size: 1.2rem; /* 按钮更大 */
  font-weight: bold;
  border-radius: 8px;
  background: linear-gradient(90deg, #1e90ff, #00bfff);
  color: #fff;
  margin-bottom: 1.5rem; /* 增加按钮之间的间距 */
  padding: 0.8rem 0; /* 增加按钮的内部间距 */
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.query-btn:hover,
.change-btn:hover {
  transform: scale(1.05);
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
}

.el-form-item {
  margin-bottom: 1.8rem; /* 增加表单项间距 */
}

.el-dialog .dialog-footer {
  text-align: center;
}
