/* 整体页面背景 */
body {
  background: linear-gradient(to bottom, #1e90ff, #1e90ff);
  font-family: 'Arial', sans-serif;
  color: #ffffff;
}

/* 搜索框样式 */
.search-form {
  margin: 3rem auto;
  padding: 2rem;
  background: #0961687c;
  border-radius: 20px;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.5);
  text-align: center;
}

.search-title {
  font-size: 1.8rem;
  font-weight: bold;
  margin-bottom: 2rem;
  color: #ecf0f1;
}

.input-box {
  border: 2px solid #1abc9c;
  border-radius: 10px;
  padding: 10px;
  font-size: 1rem;
  color: #2c3e50;
  width: 100%;
}

.searchbtn {
  background: linear-gradient(90deg, #1abc9c, #16a085);
  color: white;
  padding: 12px 30px;
  font-size: 1.4rem;
  font-weight: bold;
  border-radius: 15px;
  transition: all 0.3s ease;
  margin-top: 1rem;
}

.searchbtn:hover {
  background: linear-gradient(90deg, #16a085, #149174);
  transform: scale(1.1);
  box-shadow: 0 5px 15px rgba(22, 160, 133, 0.5);
}

/* 房间详情卡片样式 */
.box-card {
  background: #1e90ff;
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(9, 28, 134, 0.5);
  padding: 2rem;
}

.header-box {
  background: linear-gradient(90deg, #3498db, #2980b9);
  padding: 15px;
  font-size: 1.5rem;
  color: #ffffff;
  text-align: center;
  font-weight: bold;
  border-radius: 15px;
}

.user-list {
  margin-top: 2rem;
  padding: 1.5rem;
  border-radius: 15px;
  background: rgba(255, 255, 255, 0.2);
}

.user-list strong {
  color: #f39c12;
  font-size: 1.2rem;
}

p {
  font-size: 1rem;
  margin-top: 0.5rem;
  color: #bdc3c7;
}
