body {
    background: linear-gradient(to bottom, #1e3c72, #2a5298);
    font-family: 'Poppins', sans-serif;
    color: #fff;
}

.change-form {
    background: linear-gradient(to bottom right, #ffffff, #dde1e7);
    border-radius: 20px;
    padding: 3rem;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
    transition: transform 0.3s ease;
}

.change-title {
    font-size: 2rem;
    font-weight: 700;
    text-align: center;
    margin-bottom: 1.5rem;
    color: #333;
}

/* 为 el-form-item 中的 label 标签明确设置黑色字体 */
.el-form-item label {
    color: #000 !important; /* 确保覆盖其他样式 */
}

.query-btn,
.changebtn {
    width: 100%;
    font-size: 20px;
    font-weight: bold;
    color: #fff;
    border: none;
    border-radius: 25px;
    transition: all 0.3s ease;
    padding: 10px;
}

.query-btn {
    background: linear-gradient(to right, #ff7e5f, #feb47b);
}

.query-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(255, 126, 95, 0.4);
}

.changebtn {
    background: linear-gradient(to right, #43e97b, #38f9d7);
}

.changebtn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(67, 233, 123, 0.4);
}
