.user-table {
    margin: 2rem;
    background: linear-gradient(135deg, #1e3c72, #2a5298); /* 渐变背景 */
    border-radius: 10px; /* 圆角边框 */
    box-shadow: 0 8px 15px rgba(0, 0, 0, 0.2); /* 阴影效果 */
    overflow: hidden; /* 防止内容溢出 */
    padding: 1rem;
    color: #fff;
}

.el-table {
    background-color: rgba(255, 255, 255, 0.1) !important; /* 半透明背景 */
    border-radius: 10px;
    border: 1px solid rgba(255, 255, 255, 0.3); /* 边框颜色 */
}

.el-table-column .cell {
    font-size: 16px;
    font-weight: bold;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.6);
    color: #f0f0f0;
}

.el-table-column.is-center .cell {
    text-align: center;
}

.el-button {
    background: linear-gradient(90deg, #ff512f, #dd2476); /* 按钮渐变背景 */
    color: #fff;
    border: none;
    font-size: 18px;
    border-radius: 5px;
    transition: all 0.3s ease;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
}

.el-button:hover {
    background: linear-gradient(90deg, #dd2476, #ff512f);
    transform: scale(1.1); /* 放大效果 */
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.5);
}

.el-message {
    background: rgba(255, 255, 255, 0.9);
    color: #333;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
}

.el-message.success {
    background: linear-gradient(90deg, #4caf50, #8bc34a);
    color: #fff;
}

.el-message.error {
    background: linear-gradient(90deg, #f44336, #e57373);
    color: #fff;
}

.el-message.info {
    background: linear-gradient(90deg, #2196f3, #64b5f6);
    color: #fff;
}

.el-table th {
    background-color: rgba(255, 255, 255, 0.2); /* 半透明背景 */
    color: #fff;
    font-size: 16px;
    text-align: center;
    font-weight: bold;
}

.el-table td {
    background-color: rgba(255, 255, 255, 0.05); /* 半透明背景 */
    color: #e0e0e0;
    text-align: center;
    transition: all 0.3s ease;
}

.el-table td:hover {
    background-color: rgba(255, 255, 255, 0.2); /* 悬停效果 */
    color: #fff;
    transform: scale(1.05); /* 放大效果 */
}
