.user-table {
    padding: 20px;
    background: linear-gradient(135deg, #e3f2fd, #bbdefb);
    min-height: 100vh;
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    justify-content: center;
}
.order-card {
    background: #ffffff;
    border-radius: 15px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    padding: 20px;
    width: 300px;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}
.order-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.3);
}
.card-content {
    display: flex;
    flex-direction: column;
    gap: 10px;
}
.order-details h3 {
    font-size: 20px;
    color: #1565c0;
    margin-bottom: 10px;
}
.order-details p {
    font-size: 14px;
    color: #455a64;
    margin: 0;
    line-height: 1.6;
}
