.comments-container {
  background: linear-gradient(to bottom, #1e3c72, #2a5298);
  padding: 2rem;
  border-radius: 8px;
  box-shadow: 0px 4px 15px rgba(0, 0, 0, 0.2);
}

.comment-row {
  margin-bottom: 2rem;
  animation: fadeIn 0.8s ease-in-out;
}

.comment-card {
  border: 2px solid #4f8ef7;
  border-radius: 12px;
  transition: transform 0.3s, box-shadow 0.3s;
}

.comment-card:hover {
  transform: translateY(-5px);
  box-shadow: 0px 8px 20px rgba(79, 142, 247, 0.5);
}

.card-header {
  display: flex;
  align-items: center;
  gap: 1rem;
  font-size: 1.2rem;
  font-weight: bold;
  color: #fff;
}

.user-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  box-shadow: 0px 2px 8px rgba(0, 0, 0, 0.3);
}

.header-details {
  display: flex;
  flex-direction: column;
}

.username {
  color: #ffd700;
}

.user-id {
  color: black;
  font-size: 0.9rem;
}

.contact-info {
  font-size: 0.8rem;
  color: black;
}

.card-content {
  margin-top: 1rem;
  color: black;
}

.comment-info {
  font-size: 1rem;
  font-weight: 600;
}

.rating-info {
  margin-top: 0.5rem;
  font-size: 1rem;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
