export default {
    data() {
        return {
            tableData: [],
        };
    },
    methods: {
        listOrders() {
            this.axios
                .get("http://localhost:9151/admin/listOrders?orderFlags=1,2,3")
                .then((res) => {
                    this.tableData = res.data.data.map((order) => ({
                        ...order,
                        flag: this.formatFlag(order.flag),
                    }));
                })
                .catch((err) => {
                    console.log("err: " + err);
                });
        },
        formatFlag(flag) {
            switch (flag) {
                case 0:
                    return "未处理";
                case 1:
                    return "办理入住";
                case 2:
                    return "退订";
                case 3:
                    return "订单完成";
                default:
                    return "未知状态";
            }
        },
    },
    mounted() {
        this.listOrders();
    },
};
