export default {
  data() {
    return {
      change: {
        id: '',
        createTime: '',
        userName: '',
        password: '',
        email: '',
        phone: '',
        sex: '',
        jifen: '',
        state: '',
      },
      disabled: false,
      btnType: 'success',
      dialogVisible: false,
    };
  },
  methods: {
    changeBtn() {
      this.axios.post("http://localhost:9151/admin/updateUser", this.change)
        .then(res => {
          console.log(res);
          this.dialogVisible = true;
        })
        .catch(res => {
          console.log("error: " + res);
        });
    },
    queryBtn() {
      this.axios.post("http://localhost:9151/admin/getUserById?userId=" + this.change.id)
        .then(res => {
          console.log(res.data.data);
          Object.assign(this.change, res.data.data);
        })
        .catch(res => {
          console.log("err:" + res);
        });
    },
  },
};
